wwv_flow_api.create_plugin(
 p_id=>wwv_flow_api.id(52298621337914436893)
,p_plugin_type=>'REGION TYPE'
,p_name=>'APEX.FLOW.CHART.1'
,p_display_name=>'APEX Flow Chart '
,p_supported_ui_types=>'DESKTOP:JQM_SMARTPHONE'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('REGION TYPE','APEX.FLOW.CHART.1'),'')
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'FUNCTION F_AJAX (',
'    P_REGION   IN         APEX_PLUGIN.T_REGION,',
'    P_PLUGIN   IN         APEX_PLUGIN.T_PLUGIN',
') RETURN APEX_PLUGIN.T_REGION_AJAX_RESULT IS',
'    VR_RESULT       APEX_PLUGIN.T_REGION_AJAX_RESULT;',
'    VR_CUR          SYS_REFCURSOR;',
'    VR_BIND_NAMES   SYS.DBMS_SQL.VARCHAR2_TABLE;',
'BEGIN',
'    APEX_UTIL.JSON_FROM_SQL( SQLQ   => P_REGION.SOURCE );',
'    RETURN VR_RESULT;',
'END;',
'',
'FUNCTION F_RENDER (',
'    P_REGION                IN APEX_PLUGIN.T_REGION,',
'    P_PLUGIN                IN APEX_PLUGIN.T_PLUGIN,',
'    P_IS_PRINTER_FRIENDLY   IN BOOLEAN',
') RETURN APEX_PLUGIN.T_REGION_RENDER_RESULT IS',
'    VR_CONF_JSON      APEX_APPLICATION_PAGE_REGIONS.ATTRIBUTE_01%TYPE := P_REGION.ATTRIBUTE_01;',
'    VR_ITEMS2SUBMIT   APEX_APPLICATION_PAGE_REGIONS.AJAX_ITEMS_TO_SUBMIT%TYPE := APEX_PLUGIN_UTIL.PAGE_ITEM_NAMES_TO_JQUERY(P_REGION.AJAX_ITEMS_TO_SUBMIT);',
'    VR_REGION_ID      VARCHAR2(200) := ''fc-'' || P_REGION.STATIC_ID;',
'BEGIN',
'    APEX_JAVASCRIPT.ADD_LIBRARY(',
'        P_NAME        => ''preScript'',',
'        P_DIRECTORY   => P_PLUGIN.FILE_PREFIX,',
'        P_VERSION     => NULL,',
'        P_KEY         => ''prescript.src''',
'    );',
'  ',
'    APEX_JAVASCRIPT.ADD_LIBRARY(',
'        P_NAME        => ''jsonpath-0.8.0.min'',',
'        P_DIRECTORY   => P_PLUGIN.FILE_PREFIX,',
'        P_VERSION     => NULL,',
'        P_KEY         => ''jsonpath.src''',
'    );',
'    APEX_JAVASCRIPT.ADD_LIBRARY(',
'        P_NAME        => ''mxClient.min'',',
'        P_DIRECTORY   => P_PLUGIN.FILE_PREFIX,',
'        P_VERSION     => NULL,',
'        P_KEY         => ''mxClient.src''',
'    );',
'    APEX_JAVASCRIPT.ADD_LIBRARY(',
'        P_NAME        => ''script.min'',',
'        P_DIRECTORY   => P_PLUGIN.FILE_PREFIX,',
'        P_VERSION     => NULL,',
'        P_KEY         => ''flowchart.src''',
'    );',
'',
'    SYS.HTP.P( ''<div id="''',
'     || APEX_PLUGIN_UTIL.ESCAPE( VR_REGION_ID, TRUE )',
'     || ''"></div>'' );',
'',
'    APEX_JAVASCRIPT.ADD_ONLOAD_CODE( ''flow.initialize(''',
'     || APEX_JAVASCRIPT.ADD_VALUE( VR_REGION_ID, TRUE )',
'     || APEX_JAVASCRIPT.ADD_VALUE( APEX_PLUGIN.GET_AJAX_IDENTIFIER, TRUE )',
'     || APEX_JAVASCRIPT.ADD_VALUE( VR_CONF_JSON, TRUE )',
'     || APEX_JAVASCRIPT.ADD_VALUE( VR_ITEMS2SUBMIT, TRUE )',
'     || APEX_JAVASCRIPT.ADD_VALUE( P_REGION.STATIC_ID, TRUE )    ',
'     || '');'' );',
'',
'    RETURN NULL;',
'END;'))
,p_api_version=>1
,p_render_function=>'F_RENDER'
,p_ajax_function=>'F_AJAX'
,p_standard_attributes=>'SOURCE_SQL:AJAX_ITEMS_TO_SUBMIT'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_help_text=>'This plug in is an easy to use flow chart. The flow is completly data driven and you can style it as you want. The flow chart supports an manual and an auto refresh. When clicking on a vertex an event is fired, which can be catched by a dynamic actio'
||'n The flow chart is based on the mxgraph libary.'
,p_version_identifier=>'0.5.0'
,p_about_url=>'https://github.com/MarkoGranzin/ApexFlowChart'
,p_plugin_comment=>wwv_flow_string.join(wwv_flow_t_varchar2(
'- jquery   https://github.com/jquery/jquery     (License: MIT)',
'- mxgraph  https://github.com/jgraph/mxgraph    (License: Apache 2.0)  ',
'- jspath   https://goessner.net/                (License: MIT)   ',
'',
'',
unistr('the util code part is by Ronny Wei\00DF Material Cards plugin MIT')))
,p_files_version=>1228
);
