,p_help_text=>wwv_flow_string.join(wwv_flow_t_varchar2(
'<pre>',
'<h2>Json </h2>',
'{',
'	"refresh":0,',
'	"style":',
'	[',
'		{',
'			"vertexstyle":',
'			{',
'				"shape":"rectangle",',
